import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

export default function MapComponent() {
  useEffect(() => {
    // Initialize Map
    const map = L.map("map", {
      zoomControl: false,
    }).setView([22.9734, 78.6569], 5); // Center of India

    // English Tile Layer (CartoDB Positron) — NO API KEY NEEDED
    L.tileLayer(
      "https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png",
      {
        maxZoom: 19,
        attribution: "&copy; CARTO",
      }
    ).addTo(map);

    // Add Rectangle Layout (India bounding box)
    const indiaBounds = [
      [8.4, 68.7],  // Bottom-left
      [37.1, 97.25] // Top-right
    ];

    L.rectangle(indiaBounds, {
      color: "#000",
      weight: 2,
      fillOpacity: 0
    }).addTo(map);

    // Function to generate random coordinates inside India
    function randomIndiaLocation() {
      const lat = 8.4 + Math.random() * (37.1 - 8.4);
      const lon = 68.7 + Math.random() * (97.25 - 68.7);
      return [lat, lon];
    }

    // Add 25 random markers
    for (let i = 0; i < 25; i++) {
      const [lat, lon] = randomIndiaLocation();

      L.marker([lat, lon]).addTo(map)
        .bindPopup(`<b>Relief Point</b><br>Lat: ${lat.toFixed(2)}, Lon: ${lon.toFixed(2)}`);
    }

  }, []);

  return (
    <div
      id="map"
      style={{
        height: "400px",
        width: "100%",
        borderRadius: "12px",
        overflow: "hidden",
        boxShadow: "0 4px 15px rgba(0,0,0,0.2)"
      }}
    ></div>
  );
}
