import React, { useEffect } from "react";

export default function Map() {
  useEffect(() => {
    // ‚úÖ Load Google Maps dynamically
    const script = document.createElement("script");
    script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY`;
    script.async = true;
    script.onload = initMap;
    document.body.appendChild(script);

    function initMap() {
      // ‚úÖ Initialize map
      const map = new window.google.maps.Map(document.getElementById("map"), {
        center: { lat: 19.076, lng: 72.8777 }, // Mumbai center
        zoom: 6,
      });

      // ‚úÖ Sample markers (you can later replace with data from your backend)
      const locations = [
        { name: "Mumbai - Food Relief Center", lat: 19.076, lng: 72.8777 },
        { name: "Delhi - Medical Support", lat: 28.6139, lng: 77.209 },
        { name: "Pune - Donation Camp", lat: 18.5204, lng: 73.8567 },
        { name: "Chennai - Shelter Home", lat: 13.0827, lng: 80.2707 },
        { name: "Kolkata - Flood Relief Point", lat: 22.5726, lng: 88.3639 },
      ];

      // ‚úÖ Add markers
      locations.forEach((loc) => {
        const marker = new window.google.maps.Marker({
          position: { lat: loc.lat, lng: loc.lng },
          map,
          title: loc.name,
        });

        const infoWindow = new window.google.maps.InfoWindow({
          content: `<strong>${loc.name}</strong>`,
        });

        marker.addListener("click", () => infoWindow.open(map, marker));
      });
    }
  }, []);

  return (
    <>
      <style>{`
        .map-container {
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          background: #f3f4f6;
          padding: 20px;
        }

        .map-box {
          background: #e6e1ff;
          border-radius: 15px;
          padding: 30px;
          width: 90%;
          max-width: 900px;
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
          text-align: center;
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .map-box:hover {
          transform: translateY(-5px);
          box-shadow: 0 15px 30px rgba(0, 0, 0, 0.25);
        }

        .map-box h2 {
          font-size: 1.8rem;
          color: #2d2d2d;
          margin-bottom: 15px;
        }

        .map-box p {
          color: #333;
          font-size: 0.95rem;
          margin-bottom: 20px;
        }

        #map {
          width: 100%;
          height: 450px;
          border-radius: 10px;
          box-shadow: 0 4px 15px rgba(0, 0, 0, 0.15);
          transition: transform 0.3s ease;
        }

        #map:hover {
          transform: scale(1.02);
        }
      `}</style>

      <div className="map-container">
        <div className="map-box">
          <h2>üó∫Ô∏è Relief Centers & Help Locations</h2>
          <p>Explore current help centers and relief points across major cities.</p>
          <div id="map"></div>
        </div>
      </div>
    </>
  );
}
