import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import CONFIG from "../config";
import { saveToken } from "../utils/auth";

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(false);
  const nav = useNavigate();

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setMessage(null);

    try {
      const res = await fetch(`${CONFIG.API_BASE}/users/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, password, role: "victim" }),
      });

      const data = await res.json();
      if (res.ok && data.token) {
        saveToken(data.token);
        setMessage({ type: "success", text: "✅ Registered successfully!" });
        setTimeout(() => nav("/"), 1200);
      } else {
        setMessage({ type: "info", text: "Registered! Please login now." });
        setTimeout(() => nav("/login"), 1200);
      }
    } catch (err) {
      setMessage({ type: "error", text: "❌ Registration failed. Try again." });
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={styles.card}>
      <h2 style={styles.heading}>Register</h2>

      {message && (
        <div
          style={{
            ...styles.alert,
            backgroundColor:
              message.type === "success"
                ? "#28a745"
                : message.type === "info"
                ? "#007bff"
                : "#dc3545",
          }}
        >
          {message.text}
        </div>
      )}

      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Full name"
          required
          style={styles.input}
        />
        <input
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
          style={styles.input}
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
          style={styles.input}
        />
        <button type="submit" disabled={loading} style={styles.button}>
          {loading ? "Registering..." : "Register"}
        </button>
      </form>
    </div>
  );
}

const styles = {
  card: {
    maxWidth: 400,
    margin: "100px auto",
    padding: 30,
    borderRadius: 12,
    backgroundColor: "#1b1b1b",
    color: "white",
    boxShadow: "0 0 10px rgba(0,0,0,0.4)",
  },
  heading: {
    textAlign: "center",
    marginBottom: 20,
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: 12,
  },
  input: {
    padding: "10px 14px",
    borderRadius: 6,
    border: "1px solid #444",
    background: "#222",
    color: "#fff",
  },
  button: {
    padding: "10px 14px",
    borderRadius: 6,
    border: "none",
    background: "#28a745",
    color: "white",
    fontWeight: "600",
    cursor: "pointer",
    transition: "0.3s",
  },
  alert: {
    padding: "10px",
    borderRadius: 6,
    textAlign: "center",
    marginBottom: 12,
  },
};
