import React, { useState } from "react";
import CONFIG from "../config";

export default function RequestForm() {
  const [form, setForm] = useState({
    name: "",
    category: "",
    location: "",
    contactInfo: "", // ✅ updated key
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${CONFIG.API_BASE}/requests`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (res.ok) {
        setMessage("✅ Request submitted successfully!");
        setForm({ name: "", category: "", location: "", contactInfo: "" }); // ✅ reset updated key
      } else {
        setMessage("❌ " + data.message);
      }
    } catch (err) {
      setMessage("❌ Failed to send request");
    }
  };

  return (
    <div className="request-form">
      <h2>Request Help</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          placeholder="Your Name"
          required
        />
        <input
          name="category"
          value={form.category}
          onChange={handleChange}
          placeholder="Category (e.g. Food, Medical)"
          required
        />
        <input
          name="location"
          value={form.location}
          onChange={handleChange}
          placeholder="Location"
          required
        />
        <input
          name="contactInfo" // ✅ match backend field
          value={form.contactInfo}
          onChange={handleChange}
          placeholder="Contact Info"
          required
        />
        <button type="submit">Submit Request</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}
