import React, {useState} from 'react'; import { useNavigate } from 'react-router-dom'; import CONFIG from '../config'; import { saveToken } from '../utils/auth';
export default function Register(){ const [name,setName]=useState(''); const [email,setEmail]=useState(''); const [password,setPassword]=useState(''); const nav=useNavigate();
async function handleSubmit(e){ e.preventDefault(); try{ const res=await fetch(`${CONFIG.API_BASE}/users/register`, {method:'POST', headers:{'Content-Type':'application/json'}, body:JSON.stringify({name,email,password,role:'victim'})}); const data=await res.json(); if(res.ok){ if(data.token){ saveToken(data.token); nav('/'); } else { alert('Registered. Please login.'); nav('/login'); } } else throw new Error(data.message || 'Register failed'); }catch(err){ alert(err.message); } }
return (<div style={{maxWidth:540, margin:'30px auto'}} className='card'><h2>Register</h2><form onSubmit={handleSubmit} style={{display:'flex',flexDirection:'column',gap:8}}><input value={name} onChange={e=>setName(e.target.value)} placeholder='Full name' required /><input value={email} onChange={e=>setEmail(e.target.value)} placeholder='Email' required /><input type='password' value={password} onChange={e=>setPassword(e.target.value)} placeholder='Password' required /><button className='btn btn-primary' type='submit'>Register</button></form></div>); }
