import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

export default function IndiaMap() {
  useEffect(() => {
    // Initialize map centered on India
    const map = L.map("india-map").setView([22.9734, 78.6569], 5);

    // Add tile layer (OpenStreetMap)
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      maxZoom: 19,
      attribution: "&copy; OpenStreetMap contributors",
    }).addTo(map);

    // Random relief points in India
    const randomLocations = [
      { name: "Delhi", coords: [28.6139, 77.2090] },
      { name: "Mumbai", coords: [19.0760, 72.8777] },
      { name: "Bangalore", coords: [12.9716, 77.5946] },
      { name: "Chennai", coords: [13.0827, 80.2707] },
      { name: "Kolkata", coords: [22.5726, 88.3639] },
      { name: "Jaipur", coords: [26.9124, 75.7873] },
      { name: "Ahmedabad", coords: [23.0225, 72.5714] },
      { name: "Hyderabad", coords: [17.3850, 78.4867] },
      { name: "Lucknow", coords: [26.8467, 80.9462] },
    ];

    // Add markers to the map
    randomLocations.forEach((loc) => {
      L.marker(loc.coords)
        .addTo(map)
        .bindPopup(`<b>${loc.name}</b><br/>Relief Location`);
    });

    return () => map.remove();
  }, []);

  return (
    <>
      <style>{`
        .map-section {
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          background-color: #f3f4f6;
          padding: 40px;
        }

        .map-box {
          background: #1c1c1cff;
          color: white;
          width: 80%;
          max-width: 1000px;
          border-radius: 20px;
          padding: 25px;
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .map-box:hover {
          transform: translateY(-5px);
          box-shadow: 0 15px 35px rgba(0, 0, 0, 0.4);
        }

        .map-title {
          font-size: 1.8rem;
          font-weight: 600;
          text-align: center;
          margin-bottom: 20px;
          letter-spacing: 0.5px;
          color: #00e6b8;
        }

        #india-map {
          height: 500px;
          width: 100%;
          border-radius: 15px;
          overflow: hidden;
          border: 2px solid #00e6b8;
        }

        @media (max-width: 768px) {
          .map-box {
            width: 95%;
            padding: 15px;
          }
          #india-map {
            height: 400px;
          }
        }
      `}</style>

      <div className="map-section">
        <div className="map-box">
          <h2 className="map-title">üó∫Ô∏è Relief Map of India</h2>
          <div id="india-map"></div>
        </div>
      </div>
    </>
  );
}
