import React, {useState} from 'react'; import CONFIG from '../config'; import { getToken } from '../utils/auth';
export default function RequestForm(){ const [type,setType]=useState('Medical'); const [description,setDescription]=useState(''); const [lat,setLat]=useState(''); const [lng,setLng]=useState(''); const [loading,setLoading]=useState(false);
async function submit(e){ e.preventDefault(); setLoading(true); try{ const token = getToken(); const body={type,description,latitude:lat,longitude:lng}; const res = await fetch(`${CONFIG.API_BASE}/requests`, {method:'POST', headers:{'Content-Type':'application/json', ...(token?{Authorization:`Bearer ${token}`}:{} )}, body:JSON.stringify(body)}); if(!res.ok) throw new Error('Submit failed'); alert('Request submitted'); setDescription(''); setLat(''); setLng(''); }catch(e){ alert(e.message); } finally{ setLoading(false); } }
return (<div className='card' style={{maxWidth:700, margin:'24px auto'}}><h2>Report Help Request</h2><form onSubmit={submit} style={{display:'flex',flexDirection:'column',gap:10}}><select value={type} onChange={e=>setType(e.target.value)}><option>Medical</option><option>Food</option><option>Shelter</option></select><textarea placeholder='Describe the situation' value={description} onChange={e=>setDescription(e.target.value)} required /><div style={{display:'flex',gap:8}}><input placeholder='Latitude (e.g., 18.5204)' value={lat} onChange={e=>setLat(e.target.value)} required /><input placeholder='Longitude (e.g., 73.8567)' value={lng} onChange={e=>setLng(e.target.value)} required /></div><button className='btn btn-primary' disabled={loading} type='submit'>{loading?'Submitting...':'Submit Request'}</button></form></div>); }
