import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import io from "socket.io-client";
import CONFIG from "../config";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css"; // ✅ Import Leaflet CSS

const socketUrl = process.env.REACT_APP_SOCKET_URL || "http://localhost:5000";

export default function Home() {
  const [requests, setRequests] = useState([]);
  const socketRef = useRef(null);

  useEffect(() => {
    fetch(`${CONFIG.API_BASE}/requests`)
      .then((r) => r.json())
      .then(setRequests)
      .catch(() => {});
    socketRef.current = io(socketUrl);
    socketRef.current.on("connect", () => console.log("socket connected"));
    socketRef.current.on("new-request", (data) => {
      setRequests((prev) => [data, ...prev]);
    });
    return () => socketRef.current && socketRef.current.disconnect();
  }, []);

  return (
    <div>
      <section className="hero">
        <div className="hero-left">
          <div className="card">
            <h1>Connecting help with those who need it most</h1>
            <p className="lead">
              Post requests, offer resources, and let the community coordinate
              relief quickly using geolocation-powered matching.
            </p>
            <div className="actions">
              <Link to="/request" className="btn btn-primary">
                Request Help
              </Link>
              <button className="btn btn-outline">Offer Help</button>
              <Link
                to="/login"
                className="btn btn-outline"
                style={{ textDecoration: "none" }}
              >
                Login / Signup
              </Link>

              {/* ✅ Add your “View Relief Map” link here */}
              <Link to="/map" className="btn btn-outline hover-grow glow-hover">
                View Relief Map
              </Link>
            </div>

            <div className="stats">
              <div className="stat">
                <h4>Active Requests</h4>
                <p>{requests.length}</p>
              </div>
              <div className="stat">
                <h4>Volunteers</h4>
                <p>84</p>
              </div>
              <div className="stat">
                <h4>Regions Covered</h4>
                <p>12</p>
              </div>
            </div>
          </div>

          <div className="how card" style={{ marginTop: 16 }}>
            <h3>How it works</h3>
            <ol>
              <li>
                People post requests with location & category (food, shelter,
                medical).
              </li>
              <li>
                Volunteers browse nearby requests or get matched automatically.
              </li>
              <li>Admins/NGOs can monitor summaries & analytics.</li>
            </ol>
          </div>
        </div>

        <aside className="hero-right">
          <div className="card">
            <MapContainer
              center={[20.5937, 78.9629]}
              zoom={5}
              className="leaflet-container"
            >
              <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
              {requests.map((r) =>
                r.latitude && r.longitude ? (
                  <Marker
                    key={r._id}
                    position={[
                      parseFloat(r.latitude),
                      parseFloat(r.longitude),
                    ]}
                  >
                    <Popup>
                      <strong>{r.type}</strong>
                      <div>{r.description}</div>
                    </Popup>
                  </Marker>
                ) : null
              )}
            </MapContainer>
            <div style={{ padding: 12, color: "#6b7280" }}>
              Map Preview — interactive with active request pins
            </div>
          </div>
        </aside>
      </section>
      <footer>Made with ❤️ for disaster response</footer>
    </div>
  );
}
