import React, {useState} from 'react'; import { useNavigate } from 'react-router-dom'; import CONFIG from '../config'; import { saveToken } from '../utils/auth';
export default function Login(){ const [email,setEmail]=useState(''); const [password,setPassword]=useState(''); const nav=useNavigate();
async function handleSubmit(e){ e.preventDefault(); try{ const res=await fetch(`${CONFIG.API_BASE}/users/login`, {method:'POST', headers:{'Content-Type':'application/json'}, body:JSON.stringify({email,password})}); const data=await res.json(); if(res.ok && data.token){ saveToken(data.token); nav('/'); } else alert(data.message || 'Login failed'); }catch(e){alert('Login failed');} }
return (<div style={{maxWidth:540, margin:'30px auto'}} className='card'><h2>Login</h2><form onSubmit={handleSubmit} style={{display:'flex',flexDirection:'column',gap:10}}><input placeholder='Email' value={email} onChange={e=>setEmail(e.target.value)} required /><input placeholder='Password' type='password' value={password} onChange={e=>setPassword(e.target.value)} required /><button className='btn btn-primary' type='submit'>Continue</button></form></div>); }
