import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

export default function IndiaMap() {
  useEffect(() => {
    // Create map
    const map = L.map("india-map").setView([22.9734, 78.6569], 5); // Center of India

    // Add Google Maps-like tile layer (free via OSM)
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      maxZoom: 19,
      attribution: "&copy; OpenStreetMap contributors",
    }).addTo(map);

    // Random coordinates within India
    const randomLocations = [
      { name: "Delhi", coords: [28.6139, 77.2090] },
      { name: "Mumbai", coords: [19.0760, 72.8777] },
      { name: "Bangalore", coords: [12.9716, 77.5946] },
      { name: "Chennai", coords: [13.0827, 80.2707] },
      { name: "Kolkata", coords: [22.5726, 88.3639] },
      { name: "Jaipur", coords: [26.9124, 75.7873] },
      { name: "Ahmedabad", coords: [23.0225, 72.5714] },
      { name: "Hyderabad", coords: [17.3850, 78.4867] },
      { name: "Lucknow", coords: [26.8467, 80.9462] },
    ];

    // Add markers
    randomLocations.forEach((loc) => {
      L.marker(loc.coords)
        .addTo(map)
        .bindPopup(`<b>${loc.name}</b><br />Relief Location`)
        .openPopup();
    });

    return () => map.remove();
  }, []);

  return (
    <div
      id="india-map"
      style={{
        height: "90vh",
        width: "100%",
        borderRadius: "15px",
        boxShadow: "0 0 20px rgba(0,0,0,0.2)",
        marginTop: "80px",
      }}
    ></div>
  );
}
